    return (
        <form >
            <TextField 
                type='text' 
                value={state.title} 
            />

            <TextField 
                type='text' 
                value={state.description} 
            />

            <TextField 
                type='text' 
                value={state.price} 
            />      

            <TextField 
                type='text' 
                value={state.availability} 
            />  

            <TextField 
                type='submit' 
                value='Create Post' 
            />

            </form>
    );
};

export default connect(null, { addPost })(PostingForm);
