export const postingsReducer = (state = [], action) => {
    switch(action.type){
        case 'FETCH_POSTINGS':
            return action.postings
        case 'ADD_POST':
            return [...state, action.post]
        case 'DELETE_POST':
            //need to return a new array excluding the action.post
            const new_state = state.map(post => post.id !== action.post.id ? post : null)
            return new_state

        default:
            return state
    }
}
