import React, { useReducer, useState } from 'react';
import { connect } from 'react-redux'

import TextField from '../common/TextField'
import { addPost } from '../../actions/postingsActions'

const PostingForm = ({ addPost }) => {

    //create one state object

    const [state, setState] = useState({ title: '', description: '', price: '', availability: ''})

    // const [title, setTitle] = useState('')
    // const [description, setDescription] = useState('')
    // const [price, setPrice] = useState('')
    // const [availability, setAvailability] = useState('')

    const createPosting = e => {
        const post = {
            title,
            description,
            price,
            availability
        }
        e.preventDefault()
        addPost(post)
    }

    const resetForm = () => {
        setTitle('')
        setDescription('')
        setPrice('')
        setAvailability('')
    }

    return (
        <form onSubmit={createPosting}>
            <TextField 
                type='text' 
                value={state.title} 
            />

            <TextField 
                type='text' 
                value={state.description} 
            />

            <TextField 
                type='text' 
                value={state.price} 
            />      

            <TextField 
                type='text' 
                value={state.availability} 
            />  

            <TextField 
                type='submit' 
                value='Create Post' 
            />

            </form>
    );
};

export default connect(null, { addPost })(PostingForm);
